import React, {Fragment} from 'react';
import './progressive_bar.css';


class ProgressiveBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           value:'0'
        }
        this.handleChangeRange = this.handleChangeRange.bind(this);
    }

    handleChangeRange(e) {
        this.setState({
            value: e.target.value
        })
        console.log(this.state.value);
    }

    render() {
        return(
            <Fragment> 
                <div className='progress_bar_time-zero'>
                    <input type="range" min='0' max='101' value={this.state.value}className='progress_bar_time-complete' onChange={this.handleChangeRange}/>
                 </div>
            </Fragment>
        );
    }
}

export default ProgressiveBar;
