import React, {Fragment} from 'react';
import { Query } from 'react-apollo';
import { songs_query } from '../../querys/querys';
import './player.css';
import ProgressiveBar from './progress_bar/progressive_bar';

class Discografia extends React.Component {
    render () {
        return (
            <div className='discograf'>
                <Query query={songs_query}>
                    {({loading, error, data}) => {
                        if(loading) return '';
                        if(error) return `Error: ${error.message}`;
                        console.log(data.getSongs);
                        return (
                            <div className='discograf-content'>
                                <img src={data.getSongs[0].img} alt="cover" height="60" width="60"/>
                                <ul>
                                    <li className='link-2'><p>{data.getSongs[0].titulo}</p></li>
                                    <li className='link-2'><a href='google.com' className='artist-link'>{data.getSongs[0].artista}</a></li>
                                </ul>
                            </div>
                        );
                    }

                    }
                </Query>
            </div>
        );
    }
}

class PlayerControls extends React.Component {
    render() {
        
        return(
            <div className='playercontrols'>
                <div className='player-controls'>
                    <div className='player-controls__buttons'>
                        <div>Play</div>
                        <div>SkipF</div>
                        <div>SkipR</div>
                    </div>
                    <div className='player-controls__progress_bar_time'>
                        <div>0:00</div>
                        <ProgressiveBar/>
                        <div>4:34</div>
                    </div>
                </div>
            </div>
        );
    }
}

class VolumeControl extends React.Component {
    render() {
        return(
            <Fragment>
                <div className='volume'>
                    <h4>hola</h4>
                </div>  
            </Fragment>
        );
    }
}

class ComponentAudio extends React.Component {

    toggleplay () {
        let status = this.props.playStatus;
        let audio = document.getElementById('audio');
        if ( status === 'play') {
            status = 'pause';
            audio.play();
        } else {
            status = 'play';
            audio.pause();
        }
    }

    render () {
        let sourceSong = this.props.source;
        return (
            <Fragment>
                <audio id='audio'><source src={sourceSong}/></audio>
            </Fragment>
        )
    }
}

class PlayerContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            playStatus: 'play',
            currentTime: 0,
            source: ''
        }
    }

    render() {
        return(
            <Fragment>
                <div className='player'>
                    <Discografia/>
                    <PlayerControls/>
                    <VolumeControl/>
                    <ComponentAudio 
                        source={this.state.source}
                        playStatus={this.state.playStatus} />
                </div>
            </Fragment>
        );
    }
}

export default PlayerContainer;
