import React, {Fragment} from 'react';
import './progressive_bar.css';


class ProgressiveBar extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.props.updateAudioTime(e);
    }

    render() {
        const widthValue = this.props.percent;
        const songCurrentTime = this.props.songCurrentTime
        return(
            <Fragment> 
                <div className='progress_bar_time-zero'>
                    <div className='progress_bar_thumb'>
                        <input type="range" min='0' value={songCurrentTime} className='progress_bar_time-complete' onChange={this.handleChange}/>
                        <div className='progress_bar_width' style={{width: widthValue + '%' }}></div>
                    </div>
                 </div>
            </Fragment>
        );
    }
}

export default ProgressiveBar;
