import React, {Fragment} from 'react';
import { Query } from 'react-apollo';
import { discograf_query } from '../../querys/querys';

class Discografia extends React.Component {
    render () {
        return (
            <div className='discograf'>
                <Query query={discograf_query}>
                    {({loading, error, data}) => {
                        if(loading) return 'Loading...'
                        if(error) return `Error: ${error.message}`;
                        console.log(data.getSongs);
                        return (
                            <Fragment>
                                <img src={data.getSongs[0].img} alt="cover" height="70" width="70"/>
                            </Fragment>
                        );
                    }

                    }
                </Query>
            </div>
        );
    }
}

class PlayerControls extends React.Component {
    render() {
        return(
            <div className='playercontrols'>
                <span>player here</span>
            </div>
        );
    }
}

class VolumeControl extends React.Component {
    render() {
        return(
            <Fragment>
                <div className='volume'>
                    <h4>hola</h4>
                </div>  
            </Fragment>
        );
    }
}


class PlayerContainer extends React.Component {
    render() {
        return(
            <Fragment>
                <div className='player'>
                    <Discografia/>
                    <PlayerControls/>
                    <VolumeControl/>
                </div>
            </Fragment>
        );
    }
}

export default PlayerContainer;
