import React, { Fragment } from 'react';
import { Query } from 'react-apollo';
import { song_query } from '../../querys/querys'
import ProgressiveBar from './progress_bar/progressive_bar';
import PlayerControls from './buttons_player/playercontrols';

class ComponentAudio extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            playStatus: 'play',
            currentTime: 0,
            source: this.props.sourceSong,
            percent: 0
        }

        this.toggleplay = this.toggleplay.bind(this);
        this.updateTime = this.updateTime.bind(this);
        this.updateBar = this.updateBar.bind(this);
    }

    updateTime (timeStamp) {
        let currentTime = Math.floor(timeStamp)
        this.setState({
            currentTime: currentTime
        })
    }

    updateBar (percent) {
        this.setState({
            percent: percent
        })
    }

    

    toggleplay () {
        let status = this.state.playStatus;
        let audio = document.getElementById('audio');
        if ( status === 'play') {
            status = 'pause';
            audio.play();

            setInterval( () => {
                let currentTime = audio.currentTime;
                let duration = <Query query={song_query}>
                    {({loading, error, data}) => {
                        if (loading) return '';
                        if (error) return `Error: ${error.message}`;
                        return data.getSong.duracion;
                    }}
                </Query>
                let percent = (currentTime / duration) * 100;
                this.updateTime(currentTime);
                this.updateBar(percent);
            }, 1000);
        } else {
            status = 'play';
            audio.pause();
        }

        this.setState({
            playStatus: status
        })
    }

    render () {
        return (
            <Fragment>
                <audio id='audio'><source src={this.state.source}/></audio>
                <ProgressiveBar percent={this.state.percent}/>
                <PlayerControls/>
            </Fragment>
        )
    }
}


export default ComponentAudio;
