import React, {Fragment} from 'react';
import './progressive_bar.css';


class ProgressiveBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           value:'1',
           width: '1'
        }
        this.handleChangeRange = this.handleChangeRange.bind(this);
    }

    handleChangeRange(e) {
        this.setState({
            value: e.target.value,
            width: e.target.value
        })
    }

    render() {
        const sliderValue = this.state.width; 
        const sliderWidth = document.getElementsByClassName('progress_bar_time-complete');
        sliderWidth.style.width = sliderValue + '%';
        return(
            <Fragment> 
                <div className='progress_bar_time-zero'>
                    <div className='progress_bar_width'>
                        <input type="range" min='1' max='100' value={this.state.value} className='progress_bar_time-complete' onChange={this.handleChangeRange}/>
                    </div>
                 </div>
            </Fragment>
        );
    }
}

export default ProgressiveBar;
