import React, { Fragment } from 'react';
import PlayerControls from './buttons_player/playercontrols';

class ComponentAudio extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            playStatus: 'false',
            currentTime: 0,
            percent: 0
        }
        this.reactAudioPlayer = React.createRef();
        this.toggleplay = this.toggleplay.bind(this);
        this.updateTime = this.updateTime.bind(this);
        this.modifyProgressBar = this.modifyProgressBar.bind(this);
        this.updateBar = this.updateBar.bind(this);
    }

    updateTime (timeStamp) {
        let currentTime = Math.floor(timeStamp)
        this.setState({
            currentTime: currentTime
        })
    }

    modifyProgressBar(e){
        let audioP = Math.floor(e);
        //console.log(audioP);
        let audio = document.getElementById('audio');
        if(!audio.paused && !audio.ended){
            let newTime = (audioP*audio.duration) / 100;
            audio.currentTime = newTime;
        }
     
    }

    updateBar(percent){
        this.setState({
            percent: percent
        })
    }
    
    toggleplay () {
        let status = this.state.playStatus;
        if ( status === 'false') {
            status = 'true';
            audio.play();
        } else {
            status = 'play';
            audio.pause();
        }

        this.setState({
            playStatus: status
        })
    }

    render () {
        return (
            <Fragment>
                <audio id='audio' ref={this.reactAudioPlayer}><source src={this.props.source}/></audio>
                <PlayerControls percent={this.state.percent} onClick={this.toggleplay} currentTime={this.state.currentTime} onChangeMusic={this.modifyProgressBar} duration={this.props.duracion}/>
            </Fragment>
        )
    }
}


export default ComponentAudio;
