import React, { Fragment } from 'react';
import PlayerControls from './buttons_player/playercontrols';

class ComponentAudio extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            playStatus: 'false',
            audioControls: {
                songPercent: 0,
                songTime: '',
                songDuration: ''
            }
        }
        this.reactAudioPlayer = React.createRef();
        this.togglePlay = this.togglePlay.bind(this);
        this.onTimeUpdateListener = this.onTimeUpdateListener.bind(this);
    }

    onTimeUpdateListener () {
        let currentDuration = this.reactAudioPlayer.current.duration;
        let currentTime = this.reactAudioPlayer.current.currentTime;
        console.log(currentTime)
        let percent = (currentTime / currentDuration) * 100;
        //let audioControls = Object.assign({}, this.state.audioControls);
        //audioControls.songPercent = percent;
        this.setState({
            audioControls:{
                songPercent: percent
            }
        })
    }
    
    togglePlay () {
        let status = this.state.playStatus;
        if ( status === 'false') {
            setTimeout(() => {
                this.reactAudioPlayer.current.play();
            }, 0)
            status = 'true'
        } else {
            this.reactAudioPlayer.current.pause();
            status = 'false';
        }

        this.setState({
            playStatus: status
        })
    }

    render () {
        return (
            <Fragment>
                <audio id='audio' ref={this.reactAudioPlayer}><source src={this.props.source}/></audio>
                <PlayerControls percent={this.state.audioControls.songPercent} onClick={this.togglePlay} currentTime={this.state.currentTime} onChangeMusic={this.modifyProgressBar} duration={this.props.duracion}/>
            </Fragment>
        )
    }
}


export default ComponentAudio;
